var ActionBar = {

    init: function () {

        var _this = this;

        if($('.actionBar').size() > 0){

            _this.triggerPrint();

        }

    },
    triggerPrint: function(){

      $(".actionBar .print").click(function(){
      window.print();

    });

    }
};

var GoBack = {

    init: function () {

        var _this = this;
        var $goBack = $('.goBack');
        if($goBack.size() > 0){

            _this.bindEvents($goBack);

        }

    },
    bindEvents: function($element){
      var $goBackLink = $element.find("a");
      var referrerDomain = document.referrer.split("/")[2];
      var currentDomain = window.location.href.split("/")[2];
  		if (referrerDomain == currentDomain) {

          $goBackLink.click(function(e){
            window.history.back();
            e.preventDefault();
          });
      }else{

          $goBackLink.hide();
      }
    }
};

var footerNavigation = {

    init: function () {

        var _this = this;

        if($('.module-footerNavigation').size())  {

            var currentBreakpoint = DeviceSelector.getDeviceType();

			if(currentBreakpoint == "xs") {
				_this.toggleFooterNav();
			} else {
                _this.toggleFooterDestroy();
            }

        }

    },
    toggleFooterNav: function(){

        $('.module-footerNavigation .footer-linklist').hide();
        $('.module-footerNavigation h2').removeClass('open');

		$('.module-footerNavigation h2').not(".binded").addClass("binded").click(function(){

            var _this = $(this);

            _this.toggleClass('open');
			_this.next('.footer-linklist').slideToggle();

		});

    },
    toggleFooterDestroy: function(){

        $('.module-footerNavigation .footer-linklist').show();

        $('.module-footerNavigation h2').unbind('click').removeClass('binded');

    }

};

var toTopButton = {

    init: function () {

        if($('.to-top').children('a').length){

           	this.action();

        }

    },
    action: function(){

        var _this = this;

        // click event to scroll to top (smooth)
        $('.to-top').children('a').on("click", function(){
            $('html, body').animate({scrollTop : 0}, 1000);
            return false;
        });

    }

};

